package p

import "testing"

func TestP1(t *testing.T) {
	type S struct {
		I string
		O int
	}
	for i, s := range []S{
		{"(())", 0},
		{"()()", 0},
		{"(((", 3},
		{"(()(()(", 3},
		{"))(((((", 3},
		{"())", -1},
		{"))(", -1},
		{")))", -3},
		{")())())", -3},
		{X, 232},
	} {
		if o := P1(s.I); o != s.O {
			t.Error(i, s.I, s.O, o)
		}
	}
}

func TestP2(t *testing.T) {
	type S struct {
		I string
		O int
	}
	for i, s := range []S{
		{")", 1},
		{"()())", 5},
		{X, 1783},
	} {
		if o := P2(s.I); o != s.O {
			t.Error(i, s.I, s.O, o)
		}
	}
}

