package p

import "testing"

func TestP1(t *testing.T) {
	if s := P1("12345678", 1); s != "48226158" {
		t.Error(s)
	}
	if s := P1("12345678", 2); s != "34040438" {
		t.Error(s)
	}
	if s := P1("12345678", 3); s != "03415518" {
		t.Error(s)
	}
	if s := P1("12345678", 4); s != "01029498" {
		t.Error(s)
	}
	if s := P1("80871224585914546619083218645595", 100)[:8]; s != "24176176" {
		t.Error(s)
	}
	if s := P1("19617804207202209144916044189917", 100)[:8]; s != "73745418" {
		t.Error(s)
	}
	if s := P1("69317163492948606335995924319873", 100)[:8]; s != "52432133" {
		t.Error(s)
	}
	if s := P1(X, 100)[:8]; s != "94960436" {
		t.Error(s)
	}
}

